<?php	$form_id = 'form-'.time().'-'.rand(1000,9999);	$callback = (!empty($_GET['callback'])) ? $_GET['callback'] : 'callback';	$FileUpload = Upload('document', $this->config->item('base_path') . '/static/upload');		$filename = '';	if ($FileUpload['Result'] == 1) {		$filename = $FileUpload['FileDirName'];	}?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html lang="en"><head>	<link rel="stylesheet" href="<?php echo base_url(); ?>static/lib/bootstrap/css/bootstrap.min.css" />	<link rel="stylesheet" href="<?php echo base_url(); ?>static/css/panel/style.css" />	<script src="<?php echo base_url(); ?>static/js/panel/jquery.min.js"></script>	<script type="text/javascript">		var callback = window.parent.<?php echo $callback; ?>;		var form_id = '<?php echo $form_id; ?>';	</script></head><body>	<form method="post" enctype="multipart/form-data" id="<?php echo $form_id; ?>">		<input type="text" name="Callback" value="<?php echo (strlen($filename) == 0) ? 0 : 1; ?>">		<input type="text" name="FileName" value="<?php echo $filename; ?>">				<div style="padding: 10px 0 0 0;">			<a class="btn cursor browse btn-primary">Select File</a>		</div>		<div class="hide">			<div class="line"><input type="file" name="document" size="50" /></div>			<div class="line"><input type="submit" name="Submit" value="Ganti Foto" /></div>		</div>	</form>	<script type="text/javascript">		jQuery('#' + form_id + ' .browse').click(function() { jQuery('#' + form_id + ' input[name="document"]').click(); });		jQuery('#' + form_id + ' input[name="document"]').change(function() { jQuery('#' + form_id + ' input[name="Submit"]').click(); });				if (jQuery('#' + form_id + ' input[name="Callback"]').val() == 1) {			if (callback != null) {				callback({ filename: jQuery('#' + form_id + ' input[name="FileName"]').val() });			}		}	</script></body></html>